import {useState} from 'react';

export default function Form() {

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [phoneType, setPhoneType] = useState("");
    const [staff, setStaff] = useState("");
    const [bio, setBio] = useState("");
    const [emailNotifs, setEmailNotifs] = useState("");
    const [formComplete, setFormComplete] = useState(false);

    {/* ///Name, 
        ///Email, 
        ///Phone number, 
        ///Phone type: Home, Work, or Mobile (dropdown menu)
        ///Staff: Instructor or Student (radio buttons), 
        Bio (text area)
        Sign up for email notifications (checkbox)
    */}

    const handleSubmit = (e) => {
        console.log("during submission");
        e.preventDefault();
        setFormComplete(true);
        setName("");
        setEmail("");
        setPhoneNumber("");
        setStaff("");

    }

    return (
        <div>
            <h2>User Registration</h2>
            <form onSubmit={handleSubmit}>
                <label>Name: <input type="text" value={name}/></label>
                <br />
                <br />
                <label>Email: <input type="text" value={email}/></label>
                <br />
                <br />
                <label>Phone Number: <input type="text" value={phoneNumber}/></label>
                <br />
                <br />
                <label>Phone Type: <select>
                        <option value="home">Home</option>
                        <option value="work">Work</option>
                        <option value="mobile">Mobile</option>
                    </select>
                </label>
                <br />
                <br />
                <label>Staff:
                    <br />
                    <label>Instructor<input type="radio" name="staff" value="instructor"/></label>
                    <label>Student<input type="radio" name="staff" value="student"/></label>
                </label>
                <br />
                <br />
                <label>Bio:
                    <br />
                    <textarea></textarea>
                </label>
                <br />
                <br />
                <label>Email Notifications
                    <input type="checkbox" value="no"/>
                </label>
                <br />
                <br />
                <input type="submit" value="Sign Up"/>
            </form>
            <br />
            <div> Complete? { formComplete ? "Yes!" : "No :(" }</div>
        </div>
    )
}
